mapper.select:
 	select buttonId buttonId,
	       moduleid moduleid,
	       buttonUse buttonUse,
	       buttonName buttonName,
	       buttonName_en buttonName_en,
	       buttonSdescription buttonSdescription,
	       buttonIcon buttonIcon,
	       buttonOrder buttonOrder
	 from sys_rolebuttons


	 		and within_code=#{item.within_code}
		 	and buttonId=#{item.buttonId}
			and moduleid=#{item.moduleid}
			and buttonUse=#{item.buttonUse}

select 
	r.buttonId  buttonId,
	a.roleid    roleid,
	a.moduleid  moduleid,
	a.buttonuse buttonuse
from sys_rolebuttons r,sys_authorith a 
where r.within_code = a.within_code and r.moduleid = a.moduleid and a.buttonuse = r.buttonuse
and a.within_code=#{within_code}
and a.roleId=#{roleId}
and a.moduleid=#{moduleid}
and a.buttonUse=#{buttonUse}

mapper.delete:
delete from sys_authorith 



		and ( roleid=#{item.roleId}
		and moduleid=#{item.moduleid}
		and buttonUse=#{item.buttonUse} )

delete from sys_rolebuttons where buttonid in

	#{list.buttonId}

and within_code = #{withinCode}

delete
from SYS_AUTHORITH
where buttonuse = (
	select buttonuse
	from sys_rolebuttons
	where buttonid = #{buttonId}
	and moduleid=#{moduleid}
	and within_code = #{withinCode}
	)
	and within_code = #{withinCode}

update.foreach:
update sys_rolebuttons
buttonName = #{item.buttonName}, buttonName_en = #{item.buttonName_en}, buttonUse = #{item.buttonUse}, buttonIcon = #{item.buttonIcon}, buttonOrder = #{item.buttonOrder}, buttonSdescription = #{item.buttonSdescription}
where buttonId= #{item.buttonId};
